#include <unistd.h>
#include <stdio.h>
#include <sys/types.h>



int main(){

	char example[] = "This is a test for my COP4600 simple syscall number 291. Here are some other characters for this test: !*$ :).";
	char exampleother[] = "";
	printf("Here is the length of my encrypted message: ");
	printf("%i",syscall(291,example, 3, 5));
	printf("\n");
	printf("Here is my encrypted message: \n");
	printf("%s", example);
	printf("\n");
	
	
	printf("\nHere is the length of my encrypted message: ");
	printf("%i",syscall(291,example, -3, -5));
	printf("\n");
	printf("Here is my decrypted message: \n");
	printf("%s", example);
	printf("\n");
	printf("\n");

	printf("Here is an example of truncation:\n");
	printf("Here is the length of my super long string:\n%i", strlen(exampleother));
	syscall(291,exampleother,4,7);
	//printf("\nHere is my encrypted message: \n");
	//printf("%s",exampleother);
	printf("\nHere is the length of my super long string after decryption (without the null terminator):");
	printf("\n%i", syscall(291,exampleother,-4,-7));
	printf("\nHere is the decrypted string: \n");
	printf("%s", exampleother);
	printf("\n");


	return 0;
}